    async def run_single_test(self, test_info: Dict) -> Dict[str, Any]:
        """Run a single test script and capture results"""
        test_name = test_info["name"]
        script_name = test_info["script"]
        script_path = Path(__file__).parent / script_name
        logger.info(f"üöÄ Running {test_name}...")
        result = {
            "name": test_name,
            "script": script_name,
            "category": test_info["category"],
            "description": test_info["description"],
            "start_time": datetime.now().isoformat(),
            "end_time": None,
            "duration": 0,
            "success": False,
            "exit_code": -1,
            "stdout": "",
            "stderr": "",
            "error_message": None,
            "enhanced": test_info.get("enhanced", False),
            "new": test_info.get("new", False),
        }
        try:
            start_time = time.time()
            if not script_path.exists():
                if test_info.get("new", False):
                    logger.warning(
                        f"‚ö†Ô∏è  New test script {script_name} not found - skipping"
                    )
                    result["success"] = True
                    result["exit_code"] = 0
                    result["stdout"] = "Test script not implemented yet (new feature)"
                    result["stderr"] = ""
                else:
                    logger.error(f"‚ùå Test script {script_name} not found")
                    result["error_message"] = f"Test script {script_name} not found"
                    result["stderr"] = f"FileNotFoundError: {script_path}"
                end_time = time.time()
                result["duration"] = end_time - start_time
                result["end_time"] = datetime.now().isoformat()
                return result
            timeout = test_info.get("timeout", 120)
            process = await asyncio.create_subprocess_exec(
                sys.executable,
                str(script_path),
                stdout=asyncio.subprocess.PIPE,
                stderr=asyncio.subprocess.PIPE,
                cwd=Path(__file__).parent,
            )
            stdout, stderr = await process.communicate()
            end_time = time.time()
            result["duration"] = end_time - start_time
            result["end_time"] = datetime.now().isoformat()
            result["exit_code"] = process.returncode
            result["stdout"] = stdout.decode("utf-8") if stdout else ""
            result["stderr"] = stderr.decode("utf-8") if stderr else ""
            result["success"] = process.returncode == 0
            if result["success"]:
                logger.info(f"‚úÖ {test_name} - PASSED ({result['duration']:.1f}s)")
                self.results["passed_tests"] += 1
            else:
                logger.error(f"‚ùå {test_name} - FAILED ({result['duration']:.1f}s)")
                result["error_message"] = f"Exit code {process.returncode}"
                self.results["failed_tests"] += 1
        except Exception as e:
            logger.error(f"üí• {test_name} - ERROR: {e}")
            result["error_message"] = str(e)
            result["end_time"] = datetime.now().isoformat()
            result["duration"] = (
                time.time() - start_time if "start_time" in locals() else 0
            )
            self.results["failed_tests"] += 1
        return result

