    def cleanup(self):
        if self.process and self.process.poll() is None:
            self.logger.info("Terminating Python recording app")
            self.process.terminate()
            try:
                self.process.wait(timeout=10)
            except subprocess.TimeoutExpired:
                self.process.kill()

